// SPDX-License-Identifier: GPL-2.0-or-later OR MIT
pragma solidity >=0.7.0 <0.9.0;

contract FNFT {
    uint256 constant chainId = 4; // for Goerli test net. Change it to suit your network.
    mapping(uint256 => bool) private usedNounces;
    mapping(uint256 => mapping(address => uint256)) owners; // fraction smart contract[user_address] -> amount in numbers of shares
    address owner;
    struct Fnft {
        string name;
        uint256 id;
        string symbol;
        string description;
    }

    constructor() {
        owner = msg.sender;
    }

    string private constant FNFT_TYPE =
        "Fnft(string name,uint256 id,string symbol,string description)";
    string private constant EIP712_DOMAIN =
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)";

    bytes32 private constant EIP712_DOMAIN_TYPEHASH =
        keccak256(abi.encodePacked(EIP712_DOMAIN));
    bytes32 private constant FNFT_TYPEHASH =
        keccak256(abi.encodePacked(FNFT_TYPE));

    // type hashes. Hash of the following strings:
    // 1. EIP712 Domain separator.
    // 2. string describing identity type
    // 3. string describing message type (enclosed identity type description included in the string)
    address constant verifyingContract =
        0x0F990Ef7eC160f01AF7148B74CC8a86FE46c551e;

    bytes32 private constant DOMAIN_SEPARATOR =
        keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256("FractionGrid"),
                keccak256("1"),
                chainId,
                verifyingContract
            )
        );

    function getAddress2(address ad, address ad2) public pure returns (bool) {
        return ad == ad2;
    }

    function getAddress3(uint160 ad) public view returns (bool) {
        return address(ad) == msg.sender;
    }

    function hashFnft(Fnft memory fnft) private pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    "\x19\x01",
                    DOMAIN_SEPARATOR,
                    keccak256(
                        abi.encode(
                            FNFT_TYPEHASH,
                            keccak256(bytes(fnft.name)),
                            fnft.id,
                            keccak256(bytes(fnft.symbol)),
                            keccak256(bytes(fnft.description))
                        )
                    )
                )
            );
    }

    function verify(
        address signer,
        Fnft memory fnft,
        //bytes32 hash,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) public pure returns (bool) {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHash = keccak256(
            abi.encodePacked(prefix, hashFnft(fnft))
        );

        return signer == ecrecover(prefixedHash, sigV, sigR, sigS);
    }

    function getAddress() public view returns (address) {
        //bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        //bytes32 prefixedHash = keccak256(abi.encodePacked(prefix, hash));

        return msg.sender;
    }

    event TransactionVerified(
        address userAddress,
        bytes32 sigR,
        bytes32 sigS,
        bytes32 c,
        bool verified,
        address calculatedAddress
    );

    function verify2(
        address signer,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) public returns (address) {
        Fnft memory fnft = Fnft({
            name: "FractionNFT",
            id: 1000,
            symbol: "FNFT",
            description: "The best Fraction FNT"
        });

        address signerAddress = ecrecover(hashFnft(fnft), sigV, sigR, sigS);
        bool res = signer == signerAddress;

        emit TransactionVerified(
            signer,
            hashFnft(fnft),
            sigR,
            sigS,
            res,
            signerAddress
        );
        return signerAddress;
    }

    // functions to generate hash representation of the struct objects

    function acquireFnft(
        //uint256 tokenFractionAddress,
        uint256 nounce //bytes memory signature
    ) public {
        require(!usedNounces[nounce]);
        usedNounces[nounce] = true;
    }
}

library ECDSA {
    /**
     * @dev Returns the address that signed a hashed message (`hash`) with
     * `signature`. This address can then be used for verification purposes.
     *
     * The `ecrecover` EVM opcode allows for malleable (non-unique) signatures:
     * this function rejects them by requiring the `s` value to be in the lower
     * half order, and the `v` value to be either 27 or 28.
     *
     * IMPORTANT: `hash` _must_ be the result of a hash operation for the
     * verification to be secure: it is possible to craft signatures that
     * recover to arbitrary addresses for non-hashed data. A safe way to ensure
     * this is by receiving a hash of the original message (which may otherwise
     * be too long), and then calling {toEthSignedMessageHash} on it.
     *
     * Documentation for signature generation:
     * - with https://web3js.readthedocs.io/en/v1.3.4/web3-eth-accounts.html#sign[Web3.js]
     * - with https://docs.ethers.io/v5/api/signer/#Signer-signMessage[ethers]
     */
    function recover(bytes32 hash, bytes memory signature)
        internal
        pure
        returns (address)
    {
        // Check the signature length
        // - case 65: r,s,v signature (standard)
        // - case 64: r,vs signature (cf https://eips.ethereum.org/EIPS/eip-2098) _Available since v4.1._
        if (signature.length == 65) {
            bytes32 r;
            bytes32 s;
            uint8 v;
            // ecrecover takes the signature parameters, and the only way to get them
            // currently is to use assembly.
            assembly {
                r := mload(add(signature, 0x20))
                s := mload(add(signature, 0x40))
                v := byte(0, mload(add(signature, 0x60)))
            }
            return recover(hash, v, r, s);
        } else if (signature.length == 64) {
            bytes32 r;
            bytes32 vs;
            // ecrecover takes the signature parameters, and the only way to get them
            // currently is to use assembly.
            assembly {
                r := mload(add(signature, 0x20))
                vs := mload(add(signature, 0x40))
            }
            return recover(hash, r, vs);
        } else {
            revert("ECDSA: invalid signature length");
        }
    }

    /**
     * @dev Overload of {ECDSA-recover} that receives the `r` and `vs` short-signature fields separately.
     *
     * See https://eips.ethereum.org/EIPS/eip-2098[EIP-2098 short signatures]
     *
     * _Available since v4.2._
     */
    function recover(
        bytes32 hash,
        bytes32 r,
        bytes32 vs
    ) internal pure returns (address) {
        bytes32 s;
        uint8 v;
        assembly {
            s := and(
                vs,
                0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff
            )
            v := add(shr(255, vs), 27)
        }
        return recover(hash, v, r, s);
    }

    /**
     * @dev Overload of {ECDSA-recover} that receives the `v`, `r` and `s` signature fields separately.
     */
    function recover(
        bytes32 hash,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) internal pure returns (address) {
        // EIP-2 still allows signature malleability for ecrecover(). Remove this possibility and make the signature
        // unique. Appendix F in the Ethereum Yellow paper (https://ethereum.github.io/yellowpaper/paper.pdf), defines
        // the valid range for s in (281): 0 < s < secp256k1n ÷ 2 + 1, and for v in (282): v ∈ {27, 28}. Most
        // signatures from current libraries generate a unique signature with an s-value in the lower half order.
        //
        // If your library generates malleable signatures, such as s-values in the upper range, calculate a new s-value
        // with 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEBAAEDCE6AF48A03BBFD25E8CD0364141 - s1 and flip v from 27 to 28 or
        // vice versa. If your library also generates signatures with 0/1 for v instead 27/28, add 27 to v to accept
        // these malleable signatures as well.
        require(
            uint256(s) <=
                0x7FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF5D576E7357A4501DDFE92F46681B20A0,
            "ECDSA: invalid signature 's' value"
        );
        require(v == 27 || v == 28, "ECDSA: invalid signature 'v' value");

        // If the signature is valid (and not malleable), return the signer address
        address signer = ecrecover(hash, v, r, s);
        require(signer != address(0), "ECDSA: invalid signature");

        return signer;
    }

    /**
     * @dev Returns an Ethereum Signed Message, created from a `hash`. This
     * produces hash corresponding to the one signed with the
     * https://eth.wiki/json-rpc/API#eth_sign[`eth_sign`]
     * JSON-RPC method as part of EIP-191.
     *
     * See {recover}.
     */
    function toEthSignedMessageHash(bytes32 hash)
        internal
        pure
        returns (bytes32)
    {
        // 32 is the length in bytes of hash,
        // enforced by the type signature above
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
            );
    }

    /**
     * @dev Returns an Ethereum Signed Typed Data, created from a
     * `domainSeparator` and a `structHash`. This produces hash corresponding
     * to the one signed with the
     * https://eips.ethereum.org/EIPS/eip-712[`eth_signTypedData`]
     * JSON-RPC method as part of EIP-712.
     *
     * See {recover}.
     */
    function toTypedDataHash(bytes32 domainSeparator, bytes32 structHash)
        internal
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked("\x19\x01", domainSeparator, structHash)
            );
    }
}

